/* Enum that describe the different types of sound
 * that can be played. The sound with higher position 
 * have priority over the lower priority. ie an explosion
 * can override the tempo sound */ 
typedef enum
{
  INVADERS_TEMPO,
  SHOOT_SOUND,
  ENEMY_KILLED_SOUND,
  EXPLOSION_SOUND
} sound_types_e;

/* Data structure used to control the sound module */
typedef struct
{
  sound_types_e type;
  bool          active;
  const unsigned char* samples;
  size_t        length;
  size_t        counter;
} sound_control_t;

sound_control_t sound_control;

/* Init sys tick, assume that GPIO is already configured */
void sound_init(void)
{
    NVIC_ST_CTRL_R = 0;             // disable SysTick during setup
  	NVIC_ST_RELOAD_R = 0x1C68;      // reload value for 11KHz interrupt rate (assuming 80MHz)
    NVIC_ST_CTRL_R = 0x00000005;    // Disable systick for now
    sound_control.active = false;
}


/* Start playing a sound. This function check if the new sound file can be
 * started. The function enables the systick and the interrupts */
void sound_play(const unsigned char* data, size_t length, sound_types_e type)
{
  if (!sound_control.active || type >= sound_control.type)
  {
    NVIC_ST_CTRL_R = 5;
    sound_control.counter = 0;
    sound_control.length = length;
    sound_control.samples = data;
    sound_control.active = true;
    sound_control.type = type;
    DAC_PORT = sound_control.samples[0] >> 4;
    NVIC_ST_CURRENT_R = 0;
    NVIC_ST_CTRL_R = 7;
  }
}

/* Systick handler outputs new sample to DAC */
void SysTick_Handler(void)
{
  if (sound_control.active)
  {
    sound_control.counter++;

    /* Unpack the samples from the bytes. Even samples are stored in
     * the least significant nibble and odd samples are in the 
     * most significant nibble */
    if (sound_control.counter % 2)
      DAC_PORT = sound_control.samples[sound_control.counter / 2];
    else
      DAC_PORT = sound_control.samples[sound_control.counter / 2] >> 4;

    // Check for the end of the file
    if (sound_control.counter >= sound_control.length) 
    {
      sound_control.active = false;
      NVIC_ST_CTRL_R = 5;            // disable sys_timer
    }
  }
}


/* Functions to start playing the various sound files */
void sound_play_shoot(void)
{
  sound_play(SHOOT, sizeof(SHOOT) * 2, SHOOT_SOUND);
}

void sound_play_enemy_killed(void)
{
  sound_play(ENEMY_KILLED, sizeof(ENEMY_KILLED) * 2, ENEMY_KILLED_SOUND);
}

void sound_play_big_explosion(void)
{
  sound_play(BIG_EXPLOSION, sizeof(BIG_EXPLOSION) * 2, EXPLOSION_SOUND);
}
  
void sound_play_fast_invader(unsigned int t)
{
  switch (t)
  {
    case 0:
      sound_play(FAST_INVADER_0, sizeof(FAST_INVADER_0) * 2, INVADERS_TEMPO);
      break;
    case 1:
      sound_play(FAST_INVADER_1, sizeof(FAST_INVADER_1) * 2, INVADERS_TEMPO);
      break;
    case 2:
      sound_play(FAST_INVADER_2, sizeof(FAST_INVADER_2) * 2, INVADERS_TEMPO);
      break;
    default:
      sound_play(FAST_INVADER_3, sizeof(FAST_INVADER_3) * 2, INVADERS_TEMPO);
  }
}

